from django import formsfrom django.contrib.auth.forms import UserCreationFormfrom django.contrib.auth.models import Userclass SignUpForm(UserCreationForm):    full_name = forms.CharField(max_length=30, help_text='FirstName and LastName')    email = forms.EmailField(max_length=130)    phone_number = forms.CharField(max_length=15)    username = forms.CharField(max_length=30)    class Meta:        model = User        fields = ('full_name', 'username', 'email', 'phone_number', 'password1', 'password2')