from django import formsfrom django.contrib.auth.forms import UserCreationFormfrom django.contrib.auth.models import Userfrom .models import UserModelclass SignUpForm(UserCreationForm):    Gender_Choices = (        ('Male', 'Male'),        ('Female', 'Female'),    )    fullname = forms.CharField(max_length=30, help_text='FirstName and LastName')    email = forms.EmailField(max_length=130)    # phone_number = forms.CharField(max_length=15)    username = forms.CharField(max_length=30)    # gender = forms.ChoiceField(choices=Gender_Choices, required=False, widget=forms.RadioSelect)    class Meta:        model = User        fields = ('fullname', 'username', 'email', 'password1', 'password2')class UserProfileForm(forms.ModelForm):    class Meta:        model = UserModel        fields = [ 'phone_number', 'gender']