from django import formsfrom django.contrib.auth.forms import UserCreationFormfrom django.contrib.auth.models import Userfrom .models import UserModelclass SignUpForm(UserCreationForm):    email = forms.EmailField(max_length=130)    username = forms.CharField(max_length=30)    class Meta:        model = User        fields = ('username', 'email', 'password1', 'password2')class UserProfileForm(forms.ModelForm):    Gender_Choices = (        ('Male', 'Male'),        ('Female', 'Female'),    )    gender = forms.ChoiceField(choices=Gender_Choices, required=False, widget=forms.RadioSelect)    class Meta:        model = UserModel        fields = ['fullname','phone_number', 'gender']class UserUpdateForm(forms.ModelForm):    class Meta:        model = UserModel        fields = ['fullname']